    private static Logger log = LoggerFactory.getLogger(SemantriaToJSON.class);

    public static HashMap<String, Object> get_Details(Sentence details){
        HashMap<String, Object> detail_info = new HashMap<String, Object>();
        detail_info.put("isImperative", details.getIsImperative());
        detail_info.put("words", details.getWords());
        detail_info.put("isPolar",details.getIsPolar());
        return detail_info;
    }

    public static HashMap<String, Object> get_Entities(DocEntity entry){
        HashMap<String,Object> entry_info = new HashMap<String, Object>();
        entry_info.put("title",entry.getTitle());
        entry_info.put("type",entry.getType());
        entry_info.put("entryType",entry.getEntityType());
        entry_info.put("evidence",entry.getEvidence());
        entry_info.put("sentimentScore",entry.getSentimentScore());
        entry_info.put("themes",entry.getThemes());
        entry_info.put("isAbout",entry.getIsAbout());
        entry_info.put("confident",entry.getConfident());
        entry_info.put("sentimentPolarity",entry.getSentimentPolarity());
        entry_info.put("mentions",entry.getMentions());
        entry_info.put("label",entry.getLabel());
        return entry_info;
    }

    public static HashMap<String, Object> get_Topics(DocTopic topic){
        HashMap<String, Object> topic_info = new HashMap<String, Object>();
        topic_info.put("title",topic.getTitle());
        topic_info.put("type",topic.getType());
        topic_info.put("hitCount",topic.getHitCount());
        topic_info.put("sentimentScore",topic.getSentimentScore());
        topic_info.put("strengthScore",topic.getStrengthScore());
        topic_info.put("sentimentPolarity",topic.getSentimentPolarity());
        return topic_info;
    }

    public static HashMap<String,Object> get_Theme(DocTheme theme){
        HashMap<String,Object> theme_info = new HashMap<String, Object>();
        theme_info.put("title", theme.getTitle());
        theme_info.put("semtimentScore", theme.getSentimentScore());
        theme_info.put("evidence", theme.getEvidence());
        theme_info.put("isAbout", theme.getIsAbout());
        theme_info.put("strengthScore", theme.getStrengthScore());
        theme_info.put("sentimentPolarity", theme.getSentimentPolarity());
        theme_info.put("mentions", theme.getMentions());
        return theme_info;
    }

    public  static  HashMap<String, Object> get_Phrases(DocPhrase phrase){
        HashMap<String,Object> phrase_info = new HashMap<String, Object>();
        phrase_info.put("title", phrase.getTitle());
        phrase_info.put("sentimentScore", phrase.getSentimentScore());
        phrase_info.put("negativePhrase", phrase.getNegatingPhrase());
        phrase_info.put("isNegated", phrase.getIsNegated());
        phrase_info.put("sentimentPolarity", phrase.getSentimentPolarity());
        phrase_info.put("type", phrase.getType());
        return phrase_info;
    }

    public static HashMap<String,Object> get_Relation(Relation relation){
        HashMap<String,Object> relation_info = new HashMap<String, Object>();
        relation_info.put("relationType", relation.getRelationType());
        relation_info.put("confidenceScore", relation.getConfidenceScore());
        relation_info.put("entities", relation.getEntities());
        relation_info.put("type", relation.getType());
        relation_info.put("extra", relation.getExtra());
        return relation_info;
    }

    public static HashMap<String,Object> get_Opinion(Opinion opinion){
        HashMap<String, Object> opinion_info = new HashMap<String, Object>();
        opinion_info.put("quotation", opinion.getQuotation());
        opinion_info.put("type", opinion.getType());
        opinion_info.put("speaker", opinion.getSpeaker());
        opinion_info.put("topic", opinion.getTopic());
        opinion_info.put("sentimentScore", opinion.getSentimentScore());
        opinion_info.put("sentimentPolarity", opinion.getSentimentPolarity());
        return opinion_info;
    }

    @Override
    public String html2text(String hmtlString){

        ArticleExtractor extractor = ArticleExtractor.getInstance();
        String articleText = "";
        Exception excep = null;
        try {
            articleText = extractor.getText(hmtlString);
        } catch (BoilerpipeProcessingException e) {
            excep = e;
            log.debug("Caught BoilerpipeProcessingException while running ArticleExtractor.");
            log.debug("{}", e);
        }
        if (articleText.equals("")) {
            try {
                LargestContentExtractor largestExtractor = LargestContentExtractor.getInstance();
                articleText = largestExtractor.getText(hmtlString);
            } catch (BoilerpipeProcessingException e) {
                excep = e;
                log.debug("Caught BoilerpipeProcessingException while running LargestContentExtractor.");
                log.debug("{}", e);
            }
        }
        if (articleText.equals("")) {
            try {
                DefaultExtractor defaultExtractor = DefaultExtractor.getInstance();
                articleText = defaultExtractor.getText(hmtlString);
            } catch (BoilerpipeProcessingException e) {
                excep = e;
                log.debug("Caught BoilerpipeProcessingException while running DefaultExtractor.");
                log.debug("{}", e);
            }
        }
        if (articleText.equals("") && excep != null) {
            log.error("Failed to extract an article on \n\n{}\n\n", hmtlString);
            log.error("{}", excep);
        }

        System.out.print(articleText);
        return articleText;
    }

    public Float get_full_doc_sentiment_score(Object input) throws Exception{

        DocAnalyticData doc = (DocAnalyticData) input;

        return doc.getSentimentScore();
    }

    @Override
    public HashMap<String, Object> get_all_info(Object input) throws Exception{
        DocAnalyticData doc = (DocAnalyticData) input;

        HashMap<String, Object> info = new HashMap<String, Object>();
        if (doc.getDetails()!=null){
            List<Object> output = new ArrayList<Object>();
            for (Sentence detail : doc.getDetails()){
                output.add(detail);
            }
            info.put("Details", output);
        }
        if (doc.getEntities()!=null){
            List<Object> output = new ArrayList<Object>();
            for (DocEntity entry: doc.getEntities()){
                output.add(entry);
            }
            info.put("Entities", output);
        }
        if (doc.getTopics()!=null){
            List<Object> output = new ArrayList<Object>();
            for(DocTopic topic : doc.getTopics()){
                output.add(topic);
            }
            info.put("Topics", output);
        }
        if (doc.getThemes()!=null){
            List<Object> output = new ArrayList<Object>();
            for(DocTheme theme : doc.getThemes()){
                output.add(theme);
            }
            info.put("Themes", output);
        }
        if (doc.getPhrases()!=null){
            List<Object> output = new ArrayList<Object>();
            for(DocPhrase phrase : doc.getPhrases()){
                output.add(phrase);
            }
            info.put("Phrases", output);
        }
        if (doc.getRelations()!=null){
            List<Object> output = new ArrayList<Object>();
            for(Relation relation : doc.getRelations()){
                output.add(relation);
            }
            info.put("Relations", output);
        }
        if (doc.getOpinions()!=null){
            List<Object> output = new ArrayList<Object>();
            for(Opinion opinion : doc.getOpinions()){
                output.add(opinion);
            }
            info.put("Opinions", output);
        }

        info.put("id", doc.getId());
        info.put("configId", doc.getConfigId());
        info.put("status", doc.getStatus());
        info.put("sentimentScore", doc.getSentimentScore());
        info.put("summary", doc.getSummary());
        info.put("language", doc.getLanguage());
        info.put("languageScore", doc.getLanguageScore());
        info.put("sentimentPolarity", doc.getSentimentPolarity());
        info.put("sourceText", doc.getSourceText());

        return info;
    }

    @Override
    public JSONObject make_sentiment_json(String ckey, String csecert, String appName, String id, String doc, String config_id){
        Session session = Session.createSession(ckey, csecert, appName);
        Integer response = session.queueDocument( new Document(id, html2text(doc)), config_id);
        DocAnalyticData return_doc = session.getDocument(id, config_id);
        HashMap<String, Object> info = new HashMap<String, Object>();
        try{
            info = get_all_info(return_doc);
        } catch (Exception e){
            log.debug("Caught Exception while running make_sentiment_json.");
            log.debug("{}", e);
        };

        info.put("http_response", response);
        JSONObject json_info = new JSONObject(info);
        return json_info;
    }

